    <form action="Pn_Registration_Form.html" method="post">
        <div class="flex items-center min-h-screen bg-gray-50">
            <div class="flex-1 h-full max-w-4xl mx-auto bg-white rounded-lg shadow-xl">
                <div class="flex flex-col md:flex-row">
                    <div class="md:h-auto md:w-1/2">
                        <img src="/Images/developer_image_while_coding.jpg" class="object-fill w-full h-full"
                            loading="lazy" alt="Developer Image" height="200" width="300" />
                    </div>
                    <div class="flex items-center justify-center p-6 sm:p-12 md:w-1/2">
                        <div class="w-full">
                            <div class="flex justify-center">
                                <i class="fas fa-sign-in-alt fa-lg"></i>
                            </div>
                            <h1 class="mb-4 text-2xl font-bold text-center text-gray-700">
                                Say Hello &#128075;
                                {{-- Share your thoughts, Questions and Queries To the Devloper --}}
                            </h1>
                            <div>
                                <label class="block text-sm">
                                    Full Name: <span class="text-red-600 text-sm">*</span>
                                </label>
                                <input type="text"
                                    class="w-full px-4 py-2 text-sm border rounded-md focus:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-600"
                                    placeholder="Your Full Name" autofocus required maxlength="50" />
                            </div>

                            <div class="mt-4"><label for="">Gender <span
                                        class="text-red-600 text-sm">*</span></label>
                                &nbsp;
                                <select name="gender"
                                    class="border rounded-md focus:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-600 w-32 text-center"
                                    id="" required>
                                    <option value="">Select</option>
                                    <option value="male">Male</option>
                                    <option value="female">Female</option>
                                </select>
                            </div>

                            <div class="mt-4">
                                <label class="block text-sm">
                                    Age: <span class="text-red-600 text-sm">*</span>
                                </label>
                                <input type="number"
                                    class="w-full px-4 py-2 text-sm border rounded-md focus:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-600"
                                    placeholder="Your Age" maxlength="3" min="10" max="99" required />
                            </div>
                            <div class="mt-4">
                                <label class="block text-sm">
                                    Mobile No: <span class="text-red-600 text-sm">*</span>
                                </label>
                                <input type="text"
                                    class="w-full px-4 py-2 text-sm border rounded-md focus:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-600"
                                    placeholder="Mobile No." required maxlength="10" />
                            </div>
                            <div class="mt-4">
                                <label class="block text-sm">
                                    Email: <span class="text-red-600 text-sm">*</span>
                                </label>
                                <input type="email"
                                    class="w-full px-4 py-2 text-sm border rounded-md focus:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-600"
                                    placeholder="Email" required maxlength="50" />
                            </div>
                            <div>
                                <label class="block mt-4 text-sm">
                                    Address:
                                </label>
                                <textarea
                                    class="text-sm p-1 border rounded-md focus:border-blue-400 focus:outline-none focus:ring-1 focus:ring-blue-600"
                                    name="" id="" cols="50" rows="3" placeholder="Address" style="border: 1 px;"
                                    maxlength="100"></textarea>
                            </div>
                            <button type="submit"
                                class="block w-full px-4 py-2 mt-4 text-sm font-medium leading-5 text-center text-white transition-colors duration-150 bg-blue-600 border border-transparent rounded-lg active:bg-blue-600 hover:bg-blue-700 focus:outline-none focus:shadow-outline-blue"
                                href="#">
                                Submit
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </form>
    {{-- Main Content End --}}
</body>

</html>
